var ep = { 
    setValue: function (namespace, key, value) {
        var epData = JSON.parse(pm.environment.get("epData"));
        //console.log("epData[" + namespace + "]: " + epData[namespace]);
        if(epData[namespace] === null || epData[namespace] === undefined) {
            //console.log("init");
            epData[namespace] = {};
        }
        epData[namespace][key] = value;
        //console.log("epData[" + namespace + "]: " + epData[namespace]);
        //console.log("epData[" + namespace + "][" + key + "]: " + epData[namespace][key]);
        pm.environment.set("epData", JSON.stringify(epData));
    },
    getValue: function (namespace, key) {
        var epData = JSON.parse(pm.environment.get("epData"));
        if(epData[namespace] === undefined || epData[namespace] === null) {
            return null;
        } else if(epData[namespace][key] === undefined) {
            return null;
        }
        return epData[namespace][key];
    },
    usingDataFile: function() {
        return (Object.keys(pm.iterationData.toObject()).length != 0);
    },
    setSkipping: function (skip) {
        if(skip === true) {
            pm.variables.set("skipFlag", "SKIPPING");
            console.log("Skipping: TRUE");
        } else {
            pm.variables.set("skipFlag", "");
            console.log("Skipping: false");
        }
    },
    skipping: function () {
        if(pm.variables.get("skipFlag") === "SKIPPING") {
            return true;
        } else {
            return false;
        }
    }
};
